import React from 'react'

import styled from 'styled-components'

const About = styled.div `
    width: 80%;
    height: 100%;
    border: 1.25px solid white;
    border-radius: 5px;
    color: 	white;

    font-size:  1.7rem;
    font-family: 'Love Ya Like A Sister';
    box-shadow: 4px 4px 8px 4px rgb(0, 255, 255), 4px 6px 20px 4px rgba(0, 0, 0, 0.19);


`
const Wrap = styled.div `
    display: flex;
    justify-content: center;
`

const AboutMe = () => {

    return (
        <Wrap>
        <About>

            <p>
            I've been a fan of tech since an early age . I love computers and what you can achieve by using them. I am on a journey of enlightenment for web developing. You gotta love what you do and my passion for web development is strong. I have been a problem solver for much of my adult life. It's kinda what I do.  
            </p>
        </About>
        </Wrap>
    )
}

export default AboutMe
